import React ,{useState}from 'react'
// var i=1;
export default function TextForm(props) {
  
  const handleupclick=()=>{
    // console.log("Uppercase was clicked" + text);  
    var conti=text.toUpperCase();
    settext(conti);
  }
  const handlelowclick=()=>{
    // console.log("Lowercase was clicked" + text);  
    var conti=text.toLowerCase();
    settext(conti);
  }
  const handleCopy = () => {
      navigator.clipboard.writeText(text);
    }
  const speak = () => {
  let msg = new SpeechSynthesisUtterance();
  msg.text = text;
  window.speechSynthesis.speak(msg);
}

  const handleclearclick=()=>{
    var conti=("");
    settext(conti);
  }
  const handleonchange=(event)=>{
    // console.log("handle on change event fire "+ i);
    // i++;
    settext(event.target.value);  // is used to render the text else will not change the text
  }
   const [text, settext] = useState("Enter Text here");
  //  text="there"                 // wrong way to change the text


   return (
    <div>
      <div className="container">
        <h1 style={{color:props.mode==='light'?'black':'white'}}>{props.heading}</h1>
        <div className="mb-3">
        <textarea className="form-control" style={{backgroundColor:props.mode==='dark'?'grey':'white'},{color:props.mode==='dark'?'white':'black'}} placeholder="Enter the text here" value={text} id="mybox" rows="3" onChange={handleonchange}></textarea>
        </div>
        <button className="btn btn-primary mx-2" onClick={handleupclick} >Convert to Uppercase</button>  
        <button className="btn btn-primary mx-2" onClick={handlelowclick} >Convert to Lowercase</button>  
        <button className="btn btn-primary mx-2" onClick={handleclearclick} >Clear Content</button>  
        <button className="btn btn-primary mx-2" onClick={handleCopy} >Copy</button>  
         
        <button type="submit" onClick={speak} className="btn btn-warning mx-2 my-2">Speak</button>
        {/* <button className="btn btn-primary mx-2" onClick={handlecheckclick} >Find</button>   */}
      </div>
      <div className="container my-3" style={{backgroundColor:props.mode==='dark'?'grey':'white'}}>
        <h2>Your text summary</h2>
        <p>{text.split(" ").length} words and {text.length} letter</p>
        <p>{0.008*text.split(" ").length} minutes</p>
        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </div>
  )
}
