import React ,{useState}from 'react'

// var i=1;
export default function TextForm(props) {
  const handleupclick=()=>{
    // console.log("Uppercase was clicked" + text);  
    var conti=text.toUpperCase();
    settext(conti);
  }
  const handlelowclick=()=>{
    // console.log("Uppercase was clicked" + text);  
    var conti=text.toLowerCase();
    settext(conti);
  }
  const handleonchange=(event)=>{
    // console.log("handle on change event fire "+ i);
    // i++;
    settext(event.target.value);  // is used to render the text else will not change the text
  }
   const [text, settext] = useState("Enter Text here");
  //  text="there"                 // wrong way to change the text
   return (
    <div>
      <div className="container">
        <h1>{props.heading}</h1>
        <div className="mb-3">
        <textarea className="form-control" value={text} id="mybox" rows="3" onChange={handleonchange}></textarea>
        </div>
        <button className="btn btn-primary mx-2" onClick={handleupclick} >Convert to Uppercase</button>  
        <button className="btn btn-primary mx-2" onClick={handlelowclick} >Convert to Lowercase</button>  
      </div>
      <div className="container">
        <h2>Your text summary</h2>
        <p>{text.split.length()} words and {text.length()} letter</p>
      </div>
    </div>

  )
}
