import { useState } from 'react';

import './App.css';
import Navbar from "./components/Navbar";
// import About from "./components/Abouter";
import TextForm from "./components/TextForm";
// import Abouter from './components/abouter';
import Alert from "./components/Alert";

function App() {
  const [color,setcolor]=useState('white');

  const [darker,setdarker]=useState('light');

  const [alerter,setalert]=useState(null);
  const showalert=(message,type)=>{
    setalert({
      msg:message,
      type:type
    })
    setTimeout(() => {
      setalert(null);
    }, 1000);
  }
  const toggler=()=>{
    if(darker==='light'){
      setdarker('dark');
      document.body.style.backgroundColor="#05132a";
        showalert("Dark Mode has been enabled!","success")     
    }
    else{
      setdarker('light');
      document.body.style.backgroundColor="white";
        showalert("light Mode has been enabled!","success")     
    }

  }
  return (
    <> 
  <Navbar title="TextUtils" Abouttext="About" mode={darker} toggle={toggler}  color='color'/>
  <Alert alert={alerter}/>
  <div className="container my-3">
    <TextForm heading="TextUtils" mode={darker} alertor={showalert}/>
    {/* <About/> */}
  </div>
</>
  );
}
export default App;
